# Styles for toolbar buttons
def MAKELONG(l,h): return int((l & 0xFFFF) | ((h << 16) & 0xFFFF0000))
TBBS_BUTTON     =MAKELONG(TBSTYLE_BUTTON, 0) # this entry is button
TBBS_SEPARATOR  =MAKELONG(TBSTYLE_SEP, 0)    # this entry is a separator
TBBS_CHECKBOX   =MAKELONG(TBSTYLE_CHECK, 0)  # this is an auto check button
TBBS_GROUP      =MAKELONG(TBSTYLE_GROUP, 0)  # marks the start of a group
TBBS_CHECKGROUP =(TBBS_GROUP|TBBS_CHECKBOX)  # normal use of TBBS_GROUP
TBBS_DROPDOWN   =MAKELONG(TBSTYLE_DROPDOWN, 0) # drop down style
TBBS_AUTOSIZE   =MAKELONG(TBSTYLE_AUTOSIZE, 0) # autocalc button width
TBBS_NOPREFIX   =MAKELONG(TBSTYLE_NOPREFIX, 0) # no accel prefix for this button

# styles for display states
TBBS_CHECKED    =MAKELONG(0, TBSTATE_CHECKED)    # button is checked/down
TBBS_PRESSED    =MAKELONG(0, TBSTATE_PRESSED)    # button is being depressed
TBBS_DISABLED   =MAKELONG(0, TBSTATE_ENABLED)    # button is disabled
TBBS_INDETERMINATE  =MAKELONG(0, TBSTATE_INDETERMINATE)  # third state
TBBS_HIDDEN     =MAKELONG(0, TBSTATE_HIDDEN) # button is hidden
TBBS_WRAPPED    =MAKELONG(0, TBSTATE_WRAP)   # button is wrapped at this point
TBBS_ELLIPSES   =MAKELONG(0, TBSTATE_ELLIPSES)
TBBS_MARKED     =MAKELONG(0, TBSTATE_MARKED)
